import React, { Component } from "react";
import {
  Button,
  ButtonGroup,
  InputGroup,
  InputGroupAddon,
  DropdownItem,
  DropdownMenu,
  Card,
  CardBody,
  CardHeader,
  Col,
  Form,
  FormGroup,
  Input,
  Label,
  Row,
} from "reactstrap";
// feature: save test settings in json file
class FormPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      layer: "DNS",
    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleBtnClick = (btnInfo) => {
    // const { btnGroupID, btnID } = btnInfo;
    // const counters = this.state.counters.filter((c) => c.id !== counterID);
    // this.state.numCounters--;
    // const numCounters = this.state.numCounters;
    // this.setState({ numCounters });
    // this.setState({ counters });
  };

  handleClick(e) {
    const className = e.target.className;
    const inputs = document.querySelectorAll(`.${className}`);
    inputs.forEach((item) => {
      item.style.backgroundColor = "#FFFFFF";
    });
    e.target.style.backgroundColor = "blue";
    if (className === "layer-class") {
      const layer = e.target.value;
      console.log(layer);
      this.setState({ layer });
    }
  }

  render() {
    return (
      <React.Fragment>
        <Row>
          <Col xl={6} lg={12} md={12}>
            <Card id="NewTest">
              <CardHeader>New Test</CardHeader>
              <CardBody className="layer-class" data-classname="layer-class">
                {"Layer........"}
                <input
                  class="layer-class"
                  type="button"
                  value="DNS"
                  name="DNS"
                  onClick={this.handleClick}
                />
                <input
                  class="layer-class"
                  type="button"
                  value="TCP"
                  name="TCP"
                  onClick={this.handleClick}
                />
                <input
                  class="layer"
                  type="button"
                  value="UDP"
                  name="UDP"
                  onClick={this.handleClick}
                />
                <br />
                <br />
                {"Test Type "}
                <input
                  type="button"
                  value="Agent to Server"
                  name="Agent-to-Server"
                  onClick={this.handleClick}
                />
                <input
                  type="button"
                  value="Agent to Agent"
                  name="Agent-to-Agent"
                  onClick={this.handleClick}
                />
                <br />
                <br />
                <Form>
                  <FormGroup>
                    <Label for="exampleText">Test Name</Label>
                    <Input type="textarea" name="text" placeholder="Optional" />
                  </FormGroup>
                  <FormGroup>
                    <Label for="exampleText">Text Description</Label>
                    <Input
                      type="textarea"
                      name="text"
                      placeholder={this.state.layer}
                    />
                  </FormGroup>
                </Form>
              </CardBody>
            </Card>
          </Col>

          <Col xl={6} lg={12} md={12}>
            <Card id="ViewsEnabledForTest">
              <CardHeader>Form Inputs</CardHeader>
              <CardBody></CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col xl={6} lg={12} md={12}>
            <Card id="BasicConfig">
              <CardHeader>Basic Configuration</CardHeader>
              <CardBody>
                <InputGroup size="normal">
                  <InputGroupAddon addonType="prepend">Target</InputGroupAddon>
                  <Input placeholder="e.g. google.com or 192.168.0.1" />
                </InputGroup>
                <br />
                <InputGroup>
                  <InputGroupAddon addonType="prepend">
                    Protocol
                  </InputGroupAddon>
                  <Input>
                    <DropdownMenu>
                      <DropdownItem>UDP</DropdownItem>
                      <DropdownItem divider />
                      <DropdownItem>FTP</DropdownItem>
                    </DropdownMenu>
                  </Input>
                </InputGroup>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </React.Fragment>
    );
  }
}

export default FormPage;
